def to_set(string, delim=" "):
    return {name for name in string.split(delim)}

packages = [getattr(string, 'strip')("()") for string in (current_packages, old_packages,)]

installed, remote = [to_set(string) for string in packages]

not_on_remote = installed - remote
not_on_current = remote - installed

print("No. Packages not on Remote: ", len(not_on_remote))
print("No. Packages not on Current: ", len(not_on_current))
